package vn.com.mbbank.repositories.impl;import vn.com.mbbank.core.dto.response.BaseResultSelect;import vn.com.mbbank.core.repositories.impl.BaseRepositoryImpl;import vn.com.mbbank.dto.InfoChangesDTO;import org.springframework.stereotype.Repository;import vn.com.mbbank.utils.Constants;import java.util.HashMap;/** * Autogen class Repository Impl: Lớp thao tác danh sach lich su tac dong theo * nhan vien * * @author ToolGen * @date Sun Mar 20 22:23:55 ICT 2022 */@Repositorypublic class InfoChangesRepositoryImpl extends BaseRepositoryImpl  {    public BaseResultSelect getInfoChanges(InfoChangesDTO dto) {        StringBuilder sql = new StringBuilder();        sql.append("SELECT hic.data_Type dataType, "                + "    hic.data_before dataBefore, "                + "    hic.data_after dataAfter, "                + "    hic.created_by createdBy, "                + "    hic.create_date createDate "                + " FROM hr_info_changes hic "                + " WHERE employee_id = :employeeId AND NVL(hic.flag_status, " + Constants.STATUS.IS_ACTIVE + ") = :flagStatus");        HashMap<String, Object> hashMapParams = new HashMap<>();        hashMapParams.put("employeeId", dto.getEmployeeId());        hashMapParams.put("flagStatus", Constants.STATUS.IS_ACTIVE);        return getListDataAndCount(sql, hashMapParams, dto.getStartRecord(), dto.getPageSize(), InfoChangesDTO.class);    }}