package vn.com.mbbank.repositories.impl;import org.springframework.stereotype.Repository;import vn.com.mbbank.core.dto.response.BaseResultSelect;import vn.com.mbbank.core.repositories.impl.BaseRepositoryImpl;import vn.com.mbbank.dto.EmployeesDTO;import vn.com.mbbank.dto.PersonalIdentitiesDTO;import vn.com.mbbank.utils.Constants;import vn.com.mbbank.utils.QueryUtils;import vn.com.mbbank.utils.Utils;import java.util.HashMap;import java.util.List;/** * Autogen class Repository Impl: Lớp thao tác thong tin dinh danh cua nhan vien * * @author ToolGen * @date Sun Mar 20 21:50:21 ICT 2022 */@Repositorypublic class PersonalIdentitiesRepositoryImpl extends BaseRepositoryImpl {    public List<PersonalIdentitiesDTO> getPersonalIdentities(Long employeeId) {        StringBuilder sql = new StringBuilder();        sql.append(" SELECT"                + " hpi.personal_identity_id personalIdentityId,"                + " hpi.id_type_code         idTypeCode,"                + " v.label                  idTypeName,"                + " hpi.id_no                idNo,"                + " hpi.id_issue_date        idIssueDate,"                + " hpi.id_issue_place       idIssuePlace,"                + " hpi.is_main              isMain,"                + " hpi.from_date            fromDate,"                + " hpi.to_date              toDate "                + " FROM "                + " hr_personal_identities hpi"                + " LEFT JOIN v_lookup v ON v.code = hpi.id_type_code AND v.type_code = :typeCode "                + " WHERE hpi.employee_id = :employeeId AND NVL(hpi.flag_status, " + Constants.STATUS.IS_ACTIVE + ") = :flagStatus");        HashMap<String, Object> hashMapParams = new HashMap<>();        hashMapParams.put("employeeId", employeeId);        hashMapParams.put("typeCode", Constants.LOOKUP_CODES.LOAI_GIAY_TO);        hashMapParams.put("flagStatus", Constants.STATUS.IS_ACTIVE);        return (List<PersonalIdentitiesDTO>) getListData(sql, hashMapParams, null, null, PersonalIdentitiesDTO.class);    }    public BaseResultSelect searchIdentities(EmployeesDTO employeesDTO) {        StringBuilder sql = new StringBuilder("SELECT e.employee_code, " +                "       e.full_name, " +                "       mo.org_name, " +                "       vl.label, " +                "       hpi.id_no, " +                "       hpi.id_issue_date, " +                "       hpi.id_issue_place, " +                "       hpi.is_main, " +                "       e.flag_status " +                "FROM hr_employees e " +                "JOIN mp_organizations mo ON e.organization_id = mo.org_id " +                "JOIN hr_personal_identities hpi ON e.employee_id = hpi.employee_id " +                "JOIN v_lookup vl ON vl.code = e.personal_id_type_code " +                "AND vl.type_code = :typeCode " +                "WHERE 1 = 1");        HashMap<String, Object> params = new HashMap<String, Object>();        params.put("typeCode", Constants.LOOKUP_CODES.LOAI_GIAY_TO);        QueryUtils.filter(employeesDTO.getEmployeeCode(), sql, params, "e.employee_code");        QueryUtils.filter(employeesDTO.getFullName(), sql, params, "e.full_name");        QueryUtils.filterEq(employeesDTO.getEmpTypeCode(), sql, params, "e.emp_type_code");        QueryUtils.filter(employeesDTO.getFlagStatus(), sql, params, "e.flag_status");        QueryUtils.filter(employeesDTO.getListStatus(), sql, params, "e.flag_status");        if (!Utils.isNullObject(employeesDTO.getOrganizationId())) {            sql.append(" AND mo.path_id LIKE :orgId");            params.put("orgId", "%/" + employeesDTO.getOrganizationId() + "/%");        }        if (employeesDTO.getListDateOfBirth() != null && !employeesDTO.getListDateOfBirth().isEmpty()) {            QueryUtils.filterGe(Utils.convertStringToDate(employeesDTO.getListDateOfBirth().get(0)), sql, params, "e.date_of_birth");            QueryUtils.filterLe(employeesDTO.getListDateOfBirth().size() > 1 ? Utils.convertStringToDate(employeesDTO.getListDateOfBirth().get(1)) : null, sql, params, "e.date_of_birth");        }        if (employeesDTO.getListMonthOfBirth() != null && !employeesDTO.getListMonthOfBirth().isEmpty()) {            QueryUtils.filterGe(employeesDTO.getListMonthOfBirth().get(0), sql, params, "MONTH(e.date_of_birth)");            QueryUtils.filterLe(employeesDTO.getListMonthOfBirth().size() > 1 ? employeesDTO.getListMonthOfBirth().get(1) : null, sql, params, "MONTH(e.date_of_birth)");        }        if (employeesDTO.getListYearOfBirth() != null && !employeesDTO.getListYearOfBirth().isEmpty()) {            QueryUtils.filterGe(employeesDTO.getListYearOfBirth().get(0), sql, params, "YEAR(e.date_of_birth)");            QueryUtils.filterLe(employeesDTO.getListYearOfBirth().size() > 1 ? employeesDTO.getListYearOfBirth().get(1) : null, sql, params, "YEAR(e.date_of_birth)");        }        QueryUtils.filter(employeesDTO.getListGender(), sql, params, "e.gender_code");        QueryUtils.filter(employeesDTO.getListReligionCode(), sql, params, "e.religion_code");        QueryUtils.filter(employeesDTO.getListEthnicCode(), sql, params, "e.ethnic_code");        QueryUtils.filter(employeesDTO.getListMaritalStatusCode(), sql, params, "e.marital_status_code");        QueryUtils.filter(employeesDTO.getPersonalId(), sql, params, "e.personal_id");        QueryUtils.filter(employeesDTO.getTaxNo(), sql, params, "e.tax_no");        QueryUtils.filter(employeesDTO.getInsuranceNo(), sql, params, "e.insurance_no");        QueryUtils.filter(employeesDTO.getMobileNumber(), sql, params, "e.mobile_number");        QueryUtils.filter(employeesDTO.getEmail(), sql, params, "e.email");        if(employeesDTO.getListMajorLevelId() != null && !employeesDTO.getListMajorLevelId().isEmpty()){            sql.append(" AND EXISTS(select 1 from hr_employee_infos i where i.employee_id = e.employee_id and i.major_level_id IN (:listMajorLevelId))");            params.put("listMajorLevelId", employeesDTO.getListMajorLevelId());        }        QueryUtils.filter(employeesDTO.getListPositionId(), sql, params, "e.position_id");        sql.append(" ORDER BY e.employee_id");        return getListPagination(sql.toString(), params, employeesDTO.getStartRecord(), employeesDTO.getPageSize(), PersonalIdentitiesDTO.class);    }}