package vn.com.mbbank.repositories.impl;import vn.com.mbbank.core.dto.response.BaseResultSelect;import vn.com.mbbank.core.repositories.impl.BaseRepositoryImpl;import vn.com.mbbank.dto.EmployeesDTO;import vn.com.mbbank.dto.RewardRecordsDTO;import java.util.ArrayList;import org.springframework.stereotype.Repository;import java.util.List;import java.util.HashMap;import vn.com.mbbank.utils.Constants;import vn.com.mbbank.utils.QueryUtils;import vn.com.mbbank.utils.Utils;/** * Autogen class Repository Impl: Lớp thao tác danh sach khen thuong * * @author ToolGen * @date Sun Mar 20 21:33:48 ICT 2022 */@Repositorypublic class RewardRecordsRepositoryImpl extends BaseRepositoryImpl {    /**     * Lay danh sach khen thuong     *     * @param dto: params client truyen len     * @return     */    public BaseResultSelect getRewardRecords(RewardRecordsDTO dto) {        StringBuilder sql = new StringBuilder();        sql.append("SELECT a.reward_record_id rewardRecordId,"                + "    a.year year,"                + "    a.signed_date signedDate,"                + "    (select vl.label from v_lookup vl where vl.code = a.decision_level_code and vl.type_code = :decisionLevelType ) decisionLevelName,"                + "    (select vl.label from v_lookup vl where vl.code = a.method_code and vl.type_code = :methodType ) methodName,"                + "    a.reason,"                + "    a.amount_money amountMoney"                + " FROM hr_reward_records a"                + " WHERE a.employee_id = :employeeId AND NVL(a.flag_status, " + Constants.STATUS.IS_ACTIVE + ") = :flagStatus"                + " ORDER BY a.signed_date DESC");        HashMap<String, Object> hashMapParams = new HashMap<>();        hashMapParams.put("employeeId", dto.getEmployeeId());        hashMapParams.put("decisionLevelType", Constants.LOOKUP_CODES.CAP_QD_KHENTHUONG);        hashMapParams.put("methodType", Constants.LOOKUP_CODES.HINHTHUC_KHENTHUONG);        hashMapParams.put("flagStatus", Constants.STATUS.IS_ACTIVE);        return getListDataAndCount(sql, hashMapParams, Utils.NVL(dto.getStartRecord()), Utils.NVL(dto.getPageSize(), 10), RewardRecordsDTO.class);    }    /**     * Tiem kiem thong tin khen thuong     * @param employeesDTO     * @return     */    public BaseResultSelect searchRewardRecords(EmployeesDTO employeesDTO) {        StringBuilder sql = new StringBuilder(" SELECT e.employee_id,"                + " e.employee_code,"                + " e.full_name,"                + " e.flag_status,"                + " NVL(mo.full_name, mo.org_name) orgName,"                + " hrr.signed_date signedDate,"                + " hrr.year year,"                + " hrr.reason reason,"                + " vl.label methodName"                + " FROM hr_employees e"                + " LEFT JOIN mp_organizations mo ON mo.org_id = e.organization_id"                + " JOIN hr_reward_records hrr ON e.employee_id = hrr.employee_id"                + " LEFT JOIN v_lookup vl ON vl.code = hrr.method_code AND vl.type_code = :methodType"                + " WHERE 1 = 1");        HashMap<String, Object> params = new HashMap<>();        params.put("methodType", Constants.LOOKUP_CODES.HINHTHUC_KHENTHUONG);        QueryUtils.filter(employeesDTO.getEmployeeCode(), sql, params, "e.employee_code");        QueryUtils.filter(employeesDTO.getFullName(), sql, params, "e.full_name");        QueryUtils.filterEq(employeesDTO.getEmpTypeCode(), sql, params, "e.emp_type_code");        QueryUtils.filter(employeesDTO.getFlagStatus(), sql, params, "e.flag_status");        QueryUtils.filter(employeesDTO.getListStatus(), sql, params, "e.flag_status");        if (!Utils.isNullObject(employeesDTO.getOrganizationId())) {            sql.append(" AND mo.path_id LIKE :orgId");            params.put("orgId", "%/" + employeesDTO.getOrganizationId() + "/%");        }        if (employeesDTO.getListDateOfBirth() != null && !employeesDTO.getListDateOfBirth().isEmpty()) {            QueryUtils.filterGe(Utils.convertStringToDate(employeesDTO.getListDateOfBirth().get(0)), sql, params, "e.date_of_birth");            QueryUtils.filterLe(employeesDTO.getListDateOfBirth().size() > 1 ? Utils.convertStringToDate(employeesDTO.getListDateOfBirth().get(1)) : null, sql, params, "e.date_of_birth");        }        if (employeesDTO.getListMonthOfBirth() != null && !employeesDTO.getListMonthOfBirth().isEmpty()) {            QueryUtils.filterGe(employeesDTO.getListMonthOfBirth().get(0), sql, params, "MONTH(e.date_of_birth)");            QueryUtils.filterLe(employeesDTO.getListMonthOfBirth().size() > 1 ? employeesDTO.getListMonthOfBirth().get(1) : null, sql, params, "MONTH(e.date_of_birth)");        }        if (employeesDTO.getListYearOfBirth() != null && !employeesDTO.getListYearOfBirth().isEmpty()) {            QueryUtils.filterGe(employeesDTO.getListYearOfBirth().get(0), sql, params, "YEAR(e.date_of_birth)");            QueryUtils.filterLe(employeesDTO.getListYearOfBirth().size() > 1 ? employeesDTO.getListYearOfBirth().get(1) : null, sql, params, "YEAR(e.date_of_birth)");        }        QueryUtils.filter(employeesDTO.getListGender(), sql, params, "e.gender_code");        QueryUtils.filter(employeesDTO.getListReligionCode(), sql, params, "e.religion_code");        QueryUtils.filter(employeesDTO.getListEthnicCode(), sql, params, "e.ethnic_code");        QueryUtils.filter(employeesDTO.getListMaritalStatusCode(), sql, params, "e.marital_status_code");        QueryUtils.filter(employeesDTO.getPersonalId(), sql, params, "e.personal_id");        QueryUtils.filter(employeesDTO.getTaxNo(), sql, params, "e.tax_no");        QueryUtils.filter(employeesDTO.getInsuranceNo(), sql, params, "e.insurance_no");        QueryUtils.filter(employeesDTO.getMobileNumber(), sql, params, "e.mobile_number");        QueryUtils.filter(employeesDTO.getEmail(), sql, params, "e.email");        if(employeesDTO.getListMajorLevelId() != null && !employeesDTO.getListMajorLevelId().isEmpty()){            sql.append(" AND EXISTS(SELECT 1 FROM hr_employee_infos i WHERE i.employee_id = e.employee_id AND i.major_level_id IN (:listMajorLevelId))");            params.put("listMajorLevelId", employeesDTO.getListMajorLevelId());        }        QueryUtils.filter(employeesDTO.getListPositionId(), sql, params, "e.position_id");        sql.append(" ORDER BY e.employee_id");        return getListPagination(sql.toString(), params, employeesDTO.getStartRecord(), employeesDTO.getPageSize(), RewardRecordsDTO.class);    }}